import { Platform, Dimensions, PixelRatio } from 'react-native';
// import Dimensions from 'Dimensions';
// import PixelRatio from 'PixelRatio';

const deviceWidth = Dimensions.get('window').width; // 设备的宽度
const deviceHeight = Dimensions.get('window').height; // 设备的高度
// const deviceWidth = 600;
// const deviceHeight = 960;
const pixelRatio = PixelRatio.get();
const fontScale = PixelRatio.getFontScale(); // 返回字体大小缩放比例
const defaultPixel = 2; // iphone6的像素密度
// px转换成dp
const scale = Math.min(deviceHeight / 960, deviceWidth / 600); // 获取缩放比例

const color = {
  white: '#ffffff',
  black: '#000000',
  blue: '#1879dd',
  blue2: '#0b2b4c',
  gray: '#cccccc',
  red: '#df2324',
  red2: '#fc2626',
  sitColor: '#fc5923',
  sitColor2: '#4e1303',
  standColor: '#f8bc00',
  standColor2: '#483701',
  bgBlack: '#000c16',
  bgBlack2: '#0d1d2a',
  bgBlack3: '#666666',
  bgBlack4: '#333d45',
  bgBlack5: '#3e464d',
  bgBlack6: '#273143',
  bgWhite: '#ebebeb',
  bgWhite2: '#eeeeee',
  textWhite: '#ffffff',
  textBlack: '#888888',
  textBlack2: '#333333',
  textBlack3: '#cccccc',
  textBlack4: '#999999',
  textBlue: '#157efb',
};

const size = {
  deviceWidth,
  deviceHeight,
  lineWidth: 1 / pixelRatio,
  statusBarHeight: Platform === 'android' ? 24 : 20,
  navBarHeight: 44,
  widthScale: scale,
};

/**
 * 设置text为sp
 * @param size sp
 * return number dp
 */
function setSpText(number) {
  number = Math.round(number * scale * pixelRatio / fontScale);
  return number / defaultPixel;
}

function scaleSize(number) {
  number = Math.round(number * scale);
  return number;
}

const UI = { color, size, scaleSize };
module.exports = UI;
