import bleStatusManager from '../bleStatusManager';

function setRetrieveServicesStatus() {
  bleStatusManager.setRetrieveServicesStatus();
}

function getErrorInfo(status) {
  switch (status) {
    case '0':
      return ['RST', '复位'];
    case '1':
      return ['E01', 'M1过流'];
    case '2':
      return ['E02', 'M2过流'];
    case '3':
      return ['E03', 'M3过流'];
    case '4':
      return ['E04', 'M4过流'];
    case '5':
      return ['E05', 'M5过流'];
    case '6':
      return ['E06', 'M6过流'];
    case '7':
      return ['E07', 'M1 霍尔信号断'];
    case '8':
      return ['E08', 'M2 霍尔信号断'];
    case '9':
      return ['E09', 'M3 霍尔信号断'];
    case '10':
      return ['E010', 'M4 霍尔信号断'];
    case '11':
      return ['E011', 'M5 霍尔信号断'];
    case '12':
      return ['E012', 'M6 霍尔信号断'];
    case '13':
      return ['E013', 'AB 控制器通讯中断'];
    case '14':
      return ['H01', '工作制保护'];
    case '15':
      return ['H02', '过热保护'];
    case '16':
      return ['LOCK', '手控器按键锁定显示'];
    default:
      return ['RST', '复位'];
  }
}

module.exports = {
  setRetrieveServicesStatus,
  getErrorInfo,
};
