/* eslint-disable */
import screenTracker from './screenTracker';

const middlewares = [];

middlewares.push(screenTracker);

if (__DEV__) {
  const createLogger = require('redux-logger');
  middlewares.push(
    createLogger({
      collapsed: () => true,
      predicate: (_, action) => action.type !== 'EX_NAVIGATION.BATCH'
    })
  );
}

export default middlewares;
